import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import './Login.css';

function Login() {
  const { t } = useTranslation();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const login = (user) => {
    localStorage.setItem('user', JSON.stringify(user));
    window.location.href = '/';
  };
  

  const handleLogin = async () => {
    try {
      const response = await fetch('/api/users/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          email: loginEmail,
          password: loginPassword,
        }),
      });
  
      if (!response.ok) {
        throw new Error('Failed to log in');
      }
  
      const userData = await response.json();
      
      // Store the full user data (including _id) in localStorage
      localStorage.setItem('user', JSON.stringify({
        _id: userData._id,
        name: userData.user.name,
        email: userData.user.email,
      }));
  
      // Redirect to dashboard or wherever needed
      window.location.href = `/dashboard/${userData._id}`;
    } catch (error) {
      console.error('Login error:', error);
      alert(error.message);
    }
  };
  

  return (
    <div className="login-wrapper">
      <form onSubmit={handleLogin} className="form">
        <h2 className="title">{t('login_title')}</h2>
        <input
          type="email"
          className="input"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder={t('email_placeholder')}
          required
        />
        <input
          type="password"
          className="input"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder={t('password_placeholder')}
          required
        />
        <button type="submit" className="button">{t('login_button')}</button>
        <div className="forgot-password">
          <Link to="/forgot-password">{t('forgot_password_link')}</Link>
        </div>
      </form>
    </div>
  );
}

export default Login;
