import React, { useEffect, useState } from 'react';
import { useParams, Link, Route, Switch, useRouteMatch } from 'react-router-dom'; // Import necessary routing components

const Dashboard = () => {
  const { userId } = useParams();
  const [userInfo, setUserInfo] = useState(null);
  const [pastOrders, setPastOrders] = useState([]);
  const [readyForPickup, setReadyForPickup] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { path, url } = useRouteMatch(); // Get the current path and URL for nested routing

  useEffect(() => {
    if (!userId) {
      setError("User ID is missing");
      return;
    }

    const fetchDashboardData = async () => {
      try {
        const response = await fetch(`/api/dashboard/${userId}`);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setUserInfo(data.user);
        setPastOrders(data.pastOrders);
        setReadyForPickup(data.readyForPickup);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching dashboard data:', err);
        setError(err.message);
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, [userId]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="dashboard" style={styles.dashboard}>
      <div className="sidebar" style={styles.sidebar}>
        <h2>Dashboard</h2>
        <ul>
          <li>
            <Link to={`${url}/personal-info`} style={styles.sidebarLink}>
              Personal Info
            </Link>
          </li>
          <li>
            <Link to={`${url}/past-orders`} style={styles.sidebarLink}>
              Past Orders
            </Link>
          </li>
          <li>
            <Link to={`${url}/ready-for-pickup`} style={styles.sidebarLink}>
              Books Ready for Pickup
            </Link>
          </li>
        </ul>
      </div>

      <div className="content" style={styles.content}>
        <Switch>
          <Route path={`${path}/personal-info`}>
            <PersonalInfo userInfo={userInfo} />
          </Route>
          <Route path={`${path}/past-orders`}>
            <PastOrders pastOrders={pastOrders} />
          </Route>
          <Route path={`${path}/ready-for-pickup`}>
            <ReadyForPickup readyForPickup={readyForPickup} />
          </Route>
        </Switch>
      </div>
    </div>
  );
};

const PersonalInfo = ({ userInfo }) => (
  <div style={styles.section}>
    <h2>Personal Info</h2>
    <p><strong>Name:</strong> {userInfo.name}</p>
    <p><strong>Email:</strong> {userInfo.email}</p>
    <Link to="/change-password">
      <button style={styles.button}>Change Password</button>
    </Link>
  </div>
);

const PastOrders = ({ pastOrders }) => (
  <div style={styles.section}>
    <h2>Past Orders</h2>
    {pastOrders.length > 0 ? (
      <ul>
        {pastOrders.map((order, index) => (
          <li key={index} style={styles.listItem}>
            {order.title} by {order.author} (Ordered on {new Date(order.orderDate).toLocaleDateString()})
          </li>
        ))}
      </ul>
    ) : (
      <p>No past orders found.</p>
    )}
  </div>
);

const ReadyForPickup = ({ readyForPickup }) => (
  <div style={styles.section}>
    <h2>Books Ready for Pickup</h2>
    {readyForPickup.length > 0 ? (
      <ul>
        {readyForPickup.map((book, index) => (
          <li key={index} style={styles.listItem}>
            {book.items.map((item, i) => (
              <div key={i}>
                {item.title} by {item.author} - Pickup at {book.pickupLocation} on{' '}
                {new Date(book.pickupDate).toLocaleDateString()} at {book.pickupTime}
              </div>
            ))}
          </li>
        ))}
      </ul>
    ) : (
      <p>No books ready for pickup.</p>
    )}
  </div>
);

const styles = {
  dashboard: {
    display: 'flex',
    fontFamily: 'Arial, sans-serif',
    padding: '20px',
  },
  sidebar: {
    width: '200px',
    padding: '20px',
    backgroundColor: '#f5f5f5',
    borderRight: '1px solid #ddd',
  },
  sidebarLink: {
    textDecoration: 'none',
    color: '#333',
    display: 'block',
    margin: '10px 0',
    fontWeight: 'bold',
  },
  content: {
    flex: 1,
    padding: '20px',
  },
  section: {
    marginBottom: '30px',
  },
  listItem: {
    marginBottom: '10px',
  },
  button: {
    padding: '10px 20px',
    backgroundColor: '#007BFF',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
};

export default Dashboard;
