// components/Dashboard.js
import React, { useEffect, useState } from 'react';
import './Dashboard.css';

const Dashboard = () => {
  const [userInfo, setUserInfo] = useState({});
  const [pastOrders, setPastOrders] = useState([]);
  const [readyForPickup, setReadyForPickup] = useState([]);

  useEffect(() => {
    fetch('/api/dashboard')
      .then((res) => res.json())
      .then((data) => {
        console.log('Dashboard Data:', data); // Check if the data is correct
        setDashboardData(data);
      })
      .catch((error) => console.error('Error fetching dashboard data:', error));
  }, []);


  return (
    <div className="dashboard">
      <div className="user-info">
        <h2>Welcome, {userInfo.name}</h2>
        <p>Email: {userInfo.email}</p>
        <button>Change Password</button>
      </div>

      <div className="content">
        <div className="past-orders">
          <h3>Past Orders</h3>
          <ul>
            {pastOrders.map(order => (
              <li key={order.id}>
                {order.title} by {order.author} (Ordered on {order.orderDate})
              </li>
            ))}
          </ul>
        </div>

        <div className="ready-for-pickup">
          <h3>Ready for Pickup</h3>
          <ul>
            {readyForPickup.map(book => (
              <li key={book.id}>
                {book.title} by {book.author} - Pickup at {book.pickupLocation} on {book.pickupTime}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
