// components/Dashboard.js
import React, { useEffect, useState } from 'react';
import './Dashboard.css';

const Dashboard = () => {
  const [userInfo, setUserInfo] = useState({});
  const [pastOrders, setPastOrders] = useState([]);
  const [readyForPickup, setReadyForPickup] = useState([]);

  useEffect(() => {
    // Fetch user info
    fetch('/api/user-info')
      .then(res => res.json())
      .then(data => setUserInfo(data));

    // Fetch past orders
    fetch('/api/past-orders')
      .then(res => res.json())
      .then(data => setPastOrders(data));

    // Fetch books ready for pickup
    fetch('/api/ready-for-pickup')
      .then(res => res.json())
      .then(data => setReadyForPickup(data));
  }, []);

  return (
    <div className="dashboard">
      <div className="user-info">
        <h2>Welcome, {userInfo.name}</h2>
        <p>Email: {userInfo.email}</p>
        <button>Change Password</button>
      </div>

      <div className="content">
        <div className="past-orders">
          <h3>Past Orders</h3>
          <ul>
            {pastOrders.map(order => (
              <li key={order.id}>
                {order.title} by {order.author} (Ordered on {order.orderDate})
              </li>
            ))}
          </ul>
        </div>

        <div className="ready-for-pickup">
          <h3>Ready for Pickup</h3>
          <ul>
            {readyForPickup.map(book => (
              <li key={book.id}>
                {book.title} by {book.author} - Pickup at {book.pickupLocation} on {book.pickupTime}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
