import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom'; // Import Link for navigation

const Dashboard = () => {
  const { userId } = useParams(); // Extract userId from the route params
  const [userInfo, setUserInfo] = useState(null);
  const [pastOrders, setPastOrders] = useState([]);
  const [readyForPickup, setReadyForPickup] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!userId) {
      setError("User ID is missing");
      return;
    }

    // Define a function to fetch the dashboard data
    const fetchDashboardData = async () => {
      try {
        const response = await fetch(`/api/dashboard/${userId}`);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setUserInfo(data.user);
        setPastOrders(data.pastOrders);
        setReadyForPickup(data.readyForPickup);
        setLoading(false); // Set loading to false once data is fetched
      } catch (err) {
        console.error('Error fetching dashboard data:', err);
        setError(err.message);
        setLoading(false); // Even if there's an error, stop loading
      }
    };

    fetchDashboardData();
  }, [userId]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="dashboard">
      {userInfo && (
        <div className="user-info">
          <h2>Welcome, {userInfo.name}</h2>
          <p>Email: {userInfo.email}</p>
          {/* Link to the change-password page */}
          <Link to="/change-password" className="change-password-link">
            <button>Change Password</button>
          </Link>
        </div>
      )}

      <div className="content">
        <div className="past-orders">
          <h3>Past Orders</h3>
          {pastOrders.length > 0 ? (
            <ul>
              {pastOrders.map((order, index) => (
                <li key={index}>
                  {order.title} by {order.author} (Ordered on {new Date(order.orderDate).toLocaleDateString()})
                </li>
              ))}
            </ul>
          ) : (
            <p>No past orders found.</p>
          )}
        </div>

        <div className="ready-for-pickup">
          <h3>Books Ready for Pickup</h3>
          {readyForPickup.length > 0 ? (
            <ul>
              {readyForPickup.map((book, index) => (
                <li key={index}>
                  {book.items.map((item, i) => (
                    <div key={i}>
                      {item.title} by {item.author} - Pickup at {book.pickupLocation} on{' '}
                      {new Date(book.pickupDate).toLocaleDateString()} at {book.pickupTime}
                    </div>
                  ))}
                </li>
              ))}
            </ul>
          ) : (
            <p>No books ready for pickup.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
